function numberSeparator(Number) {
	var commaCounter = 10;
	Number += '';
	for (var i = 0; i < commaCounter; i++) {
		Number = Number.replace(',', '');
	}

	x = Number.split('.');
	y = x[0];
	z = x.length > 1 ? '.' + x[1] : '';
	var rgx = /(\d+)(\d{3})/;

	while (rgx.test(y)) {
		y = y.replace(rgx, '$1' + ',' + '$2');
	}
	commaCounter++;
	return y + z;
}
